import Optional from "./Optional";
import Stream from "./Stream";

export { Optional,Stream };

export  default Optional;

import {executeAsyncWith, executeWith, flattenResults} from "./shared";

export default class Optional<Type> {

    functions: Function[] = [];

    getFunctions(): Function[] {
        return this.functions;
    }
    map<U>(fn: (value:any ) => U) {
        this.functions.push((arrayedInput: any) =>
            Array.prototype.map.call(arrayedInput, fn)
        );
        return this;
    }

    filter(fn:(value: any) => Boolean) {
        this.functions.push((arrayedInput: any) =>
            Array.prototype.filter.call(arrayedInput, fn)
        );
        return this;
    }

    peek(fn: (value:any ) => Type) {
        this.functions.push((arrayedInput: any) =>
            Array.prototype.map.call(arrayedInput, (data) => {
                fn(data);
                return data;
            })
        );
        return this;
    }

    flatmap<U>(fn: (value:any ) => U) {
        this.functions.push((arrayedInput: any) =>
            Array.prototype.map.call(arrayedInput, fn)
        );
        this.functions.push((arrayedInput: any) =>
            Array.prototype.map.call(arrayedInput, flattenResults)
        );

        return this;
    }

    flatten() {
        this.functions.push((arrayedInput: any) =>
            Array.prototype.map.call(arrayedInput, flattenResults)
        );
        return this;
    }
    constructor(private input: Type) {

    }
    public stream():any[] {
        // tslint:disable-next-line: no-console
        console.warn("stream deprecated , will be removed in 3.x");
        const result = this.execute();

        if (undefined === result || null === result) return [];


        if (Array.isArray(result)) return result;
        else return formatInput(result);
    }
    public async getAsync() {
        const finalOutput = await executeAsyncWith(formatInput(this.input), this.getFunctions());

        // @ts-ignore
        const asyncResult = getSingleResult(finalOutput);
        return asyncResult;
    }

    public async toAsync() {
        const asyncResult = await this.getAsync();
        return new Optional(asyncResult);
    }

    execute() {

        const result= executeWith(formatInput(this.input), this.getFunctions());
        return getSingleResult(result);
    }

    public get() {
        return this.execute();
    }

    public orElse(defaultValue: any) {
        const result = this.execute();
        return result ? result : defaultValue;
    }

    public isPresent() {
        const result = this.execute();
        return result ? true : false;
    }

    public ifPresent<U>(fn: (value:any ) => U) {
        const result = this.execute();
        if (result) return fn(result);
    }

    public ifPresentOrElse<U>(fn: (value:any ) => U, elseFn: any) {
        const result = this.execute();
        if (result) return fn(result);
        else return elseFn();
    }

    public static of<Type>(input:Type): Optional<Type> {

        return new Optional(input);
    }
}

function formatInput(input:any) {
    if (!!input) return [input];
    return [];
}
 function getSingleResult(arr: any[]) {
    if (arr.length === 0) return null;
    return arr[0];
}

export  function flattenResults(result: any) {
    const isOptional =
        result.hasOwnProperty("input") && result.hasOwnProperty("functions");

    // tslint:disable-next-line: no-console
    console.assert(isOptional, {
        result,
        errorMsg: "flatten should be called with an optional",
    });

    return result.get();
}

export function executeAsyncWith(input:any,functions:Function[] ){
    return functions.reduce(
        (p: Promise<any>, currentFunction: Function) => {
            return p.then((item) => {
                return new Promise((resolve) => {
                    return Promise.resolve(item).then((val) => {
                        const result = val.length > 0 ? currentFunction(val) : val;
                        resolve(Promise.all(result));
                    });
                });
            });
        },
        Promise.resolve(Promise.all(input))
    );
}
export function executeWith(input:any,functions:Function[] ){
    const finalOutput = functions.reduce(
        (acc: any, currentFunction: Function) => {
            const value = currentFunction(acc);
            return acc.length > 0 ? value : acc;
        },
        input
    );

    return finalOutput;


}

import {executeAsyncWith, executeWith, flattenResults} from "./shared";


export  default class Stream<Type> {


    functions: Function[] = [];

    getFunctions(): Function[] {
        return this.functions;
    }


    filter(fn:(value: any) => Boolean) {
        this.functions.push((arrayedInput: any) =>
            Array.prototype.filter.call(arrayedInput, fn)
        );
        return this;
    }

    peek(fn: (value:any ) => Type) {
        this.functions.push((arrayedInput: any) =>
            Array.prototype.map.call(arrayedInput, (data) => {
                fn(data);
                return data;
            })
        );
        return this;
    }

    flatmap<U>(fn: (value:any ) => U) {
        this.functions.push((arrayedInput: any) =>
            Array.prototype.map.call(arrayedInput, fn)
        );
        this.functions.push((arrayedInput: any) =>
            Array.prototype.map.call(arrayedInput, flattenResults)
        );

        return this;
    }

    flatten() {
        this.functions.push((arrayedInput: any) =>
            Array.prototype.map.call(arrayedInput, flattenResults)
        );
        return this;
    }
    public async getAsync() {
        const finalOutput = await executeAsyncWith(formatInput(this.input), this.getFunctions());
// result should always be array
        return finalOutput;
    }

    public async toAsync() {
        const asyncResult = await this.getAsync();
        // @ts-ignore
        return new Stream(formatInput(asyncResult));
    }


    take(n:number) {
        this.functions.push((arrayedInput: any) =>
            Array.prototype.filter.call(arrayedInput, (element,index)=>index<n)
        );
        return this;
    }

    skip(n:number) {
        this.functions.push((arrayedInput: any) =>
            Array.prototype.filter.call(arrayedInput, (element,index)=>index>n-1)
        );
        return this;
    }

    private pushFunctionToGetDataAt(n:number){

        this.functions.push((arrayedInput: any) =>
            Array.prototype.filter.call(arrayedInput, (element,index)=>index===n-1)
        );
    }
    public first() {
        this.pushFunctionToGetDataAt(1);
        return this;
    }
    public last() {
        this.functions.push((arrayedInput: any) =>
            Array.prototype.filter.call(arrayedInput, (element,index,arr)=>index===arr.length-1)
        );
        return this;
    }
    public nth(n:number) {
        this.pushFunctionToGetDataAt(n);
        return this;
    }


    public static of(input: any[]) {
        return new Stream(input);
    }
    constructor(private input: any[]) {

    }
    map<U>(fn: (value:any ) => U) {
        this.functions.push((arrayedInput: any) =>
            Array.prototype.map.call(arrayedInput, fn)
        );
        return this;
    }
    execute() {

        return executeWith(formatInput(this.input), this.getFunctions());
    }

    public get() {
        return this.execute();
    }

}
function formatInput(input:any) {
    if (undefined === input || null === input ) return [];

    if (Array.isArray(input) === false) {
        // tslint:disable-next-line: no-console
        console.warn("input is not an array,converting as array");
        return [input];
    }
    return input;

}
